Query:
PREFIX type: <http://dbpedia.org/class/yago/>
PREFIX prop: <http://dbpedia.org/property/>
PREFIX geo: <http://www.w3.org/2003/01/geo/wgs84_pos#>
PREFIX dbo: <http://dbpedia.org/ontology/>

SELECT ?country_name ?population ?capital ?caplat ?caplong 
WHERE {
 ?country a type:EuropeanCountries ;
 rdfs:label ?country_name ;
 prop:populationEstimate ?population .
 ?country  dbo:capital ?capital .
  OPTIONAL {
   ?capital geo:lat ?caplat ;
   geo:long ?caplong .
  }
 FILTER (?population > <populateEstimated> && langMatches(lang(?country_name), "EN")) .
} ORDER BY DESC(?population)

PREFIX dbpedia-owl: <http://dbpedia.org/ontology/>
PREFIX acco: <http://purl.org/acco/ns#>
PREFIX dbpedia: <http://dbpedia.org/resource/>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>

SELECT DISTINCT (COUNT(*) as ?terrace) WHERE {
	?s dbpedia-owl:location dbpedia:<city> ;
	rdf:type acco:<places>;
	acco:feature "terrace" .
}
